import { edk } from '@ditsmod/core';
import { ReferenceObject, XParameterObject } from '@ts-stack/openapi-spec';

/**
 * This metadata is generated by `ROUTES_EXTENSIONS` group, and available via DI per
 * a route.
 */
export interface OasRouteMeta extends edk.RouteMeta {
  parameters?: (ReferenceObject | XParameterObject<any>)[];
}
